    <script>
    $(document).ready(function() {
        $("#comm-fldr .c-t ul li a").click(function() {
            $("#comm-fldr .c-t ul li").removeClass("active");
            $(this).parent("li").addClass("active");
        });

        $(document).mouseup(function(e) {
            var container = $(".dropdown");
            if (!container.is(e.target) && container.has(e.target).length === 0) {
                container.removeClass("open");
            }
        });
        $(document).on("click", ".tab-item", function() {
            $(".tab-item").removeClass("active");
            $(this).addClass("active");
        });

        $(document).on("click", `button[data-control="dropdown"]`, function() {
            $(".dropdown").removeClass("open")
            $(this).parents(".dropdown").addClass("open");
        });

        $(document).on("click", ".more .hiding", function() {
            $(this).removeClass("hiding").addClass("showing");
            $(".filters[data-role='filters-advanced']").removeClass("hide");
        });

        $(document).on("click", ".showing", function() {
            $(this).removeClass("showing").addClass("hiding");
            $(".filters[data-role='filters-advanced']").addClass("hide");
        });

        $(document).on("click", "#comm-new-msg", function() {
            $("#comm-tlbx").toggleClass("open")
        })
    });
    </script>